let doorImage1 = document.getElementById('door1');
let doorImage2 = document.getElementById('door2');
let doorImage3 = document.getElementById('door3');
let startButton = document.getElementById('start')




let niuHusky="data:image/png;base64,"

let scoobyDoo="http://www.techauk.com/wp-content/uploads/2018/01/scooby-doo-shocked.png"

let courage="https://images-na.ssl-images-amazon.com/images/I/41xgjPGILRL._SX425_.jpg"



let closedDoorPath="https://s3.amazonaws.com/codecademy-content/projects/chore-door/images/closed_door.svg"


let numClosedDoors = 3;
let openDoor1;
let openDoor2;
let openDoor3;
let currentlyPlaying = true;

const isBot = (door) => {
  if(door.src === niuHusky) {
    return true;
  } else {
    return false;
}
}

const isClicked = (door) => {
  if (door.src === closedDoorPath) {
  return false;
} else {
  return true;
}
}

const playDoor = (door) => {
  numClosedDoors--;
  if (numClosedDoors === 0) {
  gameOver('win');
}
  else if (isBot(door)) {
  gameOver('lose')
} 
}

const randomChoreDoorGenerator = () => {
   choreDoor = Math.floor(Math.random() * numClosedDoors);
  if(choreDoor === 0){
  openDoor1 = niuHusky;
  openDoor2 = scoobyDoo;
  openDoor3 = courage;
} else if (choreDoor === 1) { 
  openDoor2 = niuHusky;
  openDoor1 = scoobyDoo;
  openDoor3 = courage;
} else { (choreDoor === 2)
  openDoor3 = niuHusky;
  openDoor1 = scoobyDoo;
  openDoor2 = courage;
}
}

door1.onclick = () => {
  if(currentlyPlaying && !isClicked(doorImage1)) {
  doorImage1.src=openDoor1;
  playDoor(door1);
   }
  }

door2.onclick = () => {
  if(currentlyPlaying && !isClicked(doorImage2)) {
  doorImage2.src=openDoor2;
  playDoor(door2);
  }
}

door3.onclick = () => {
  if(currentlyPlaying && !isClicked(doorImage3)) {
  doorImage3.src=openDoor3;
  playDoor(door3);
  }
}

startButton.onclick = () => {
  if(!currentlyPlaying) {
  startRound();
  }
}

const startRound = () => {
  door1.src = closedDoorPath;
  door2.src = closedDoorPath;
  door3.src = closedDoorPath;
  numClosedDoors = 3;
  currentlyPlaying = true;
  startButton.innerHTML = 'Good Luck!'
  randomChoreDoorGenerator();
}

const gameOver = (status) => {
  if (status === 'win') {
  startButton.innerHTML = 'You win! Play again?';
}else {
  startButton.innerHTML = 'Game over! Play again?'
}
  currentlyPlaying = false;
  }


startRound();